import { useEffect, useState } from "react"
function SingleProduct({name}) {
    const [singleProduct, setSingleProduct] = useState([])
    useEffect(()=>{
      fetchData()
    },[])
  
    const apiUrl = 'https://appsalabackend.onrender.com/products'
  
    const fetchData = async() =>{
      const response = await fetch(apiUrl)
      const data = await response.json()
      // console.log(products.data)
      const foundProducts = data.filter((product) => product.name === name);
      console.log(foundProducts)
    }
    // const foundProducts = data.filter((product) => product.name === name);
    // setSingleProduct(foundProducts)

  return (
    <div className="product-text">
    <p className="product-info-heading">{singleProduct.name}</p>
    <p style={{color: "#454545;"}}>{singleProduct.review}
    </p>
    <p>749  Follows</p>
    </div>
  )
}

export default SingleProduct
