import React, { useState, useEffect } from "react";
import "./Addcompany.css"; // Add this CSS file for styling
import ReactQuill from 'react-quill';
import 'react-quill/dist/quill.snow.css';
import './Review.css';




const Addcompany = () => {

  const [formData, setFormData] = useState({
    name: "",
    shortname: "",
    slug: "",
    rating: {

    },
    logo: null,
    tag: [],
    Category: "",
    shortDescription: "",
    longDescription: "",
    review: "",
    sellerDetails: {
      seller: "",
      Website: "",
      companyWebsite: "",
      yearFounded: "",
      HQLocation: "",
      socialmedia: {
        twitter: "",
        linkedInPage: "",
      },
    },
    appPricing: [],
    appMedia: {
      officialScreenshots: [],
      officialVideos: [],
      officialDownloads: [],
    },
  });

  const [appPricing, setAppPricing] = useState([
    {
      appName: '',
      price: 0,
      description: '',
    },
  ]);

  const [appMedia, setAppMedia] = useState({
    officialScreenshots: [],
    officialVideos: [],
    officialDownloads: [],
  });

  const handleAddAppPricing = () => {
    setAppPricing([
      ...appPricing,
      {
        appName: '',
        price: 0,
        description: '',
      },
    ]);
  };

  const handleAppPricingChange = (event, index) => {
    const { name, value } = event.target;
    const updatedAppPricing = appPricing.map((pricing, i) => {
      if (i === index) {
        return {
          ...pricing,
          [name]: value,
        };
      }
      return pricing;
    });

    setAppPricing(updatedAppPricing);
  };

  const handleAppMediaChange = (event, field) => {
    const file = event.target.files[0];
    if (!file) return;

    const reader = new FileReader();
    reader.readAsDataURL(file);

    reader.onload = () => {
      setAppMedia({
        ...appMedia,
        [field]: [...appMedia[field], reader.result],
      });
    };
  };



  const handleChange = (e) => {
    const { name, value } = e.target;

    if (name === "twitter") {
      // Update the Twitter field in socialmedia
      setFormData((prevFormData) => ({
        ...prevFormData,
        sellerDetails: {
          ...prevFormData.sellerDetails,
          socialmedia: {
            ...prevFormData.sellerDetails.socialmedia,
            twitter: value,
          },
        },
      }));
    } else if (name === "linkedInPage") {
