    const numericValue = Number(value);

    // Check if the value is a valid number between 0 and 5
    if (!isNaN(numericValue) && numericValue >= 0 && numericValue <= 5) {
      setFormData((prevFormData) => ({
        ...prevFormData,
        rating: {
          ...prevFormData.rating,
          [name]: numericValue,
        },
