import { useState,useEffect } from "react";
function Menu({childs}) {
  const [subCategories, setSubCategories] = useState([])
  const [subMenu, setSubMenu] = useState(false)
  const [categories, setCategories] = useState([])

  const handleMouseLeave = () => {
    setSubMenu(false)
  };

  const handleMouseEnter = (item) => {
    setSubMenu(true)  
    childs?.data?.forEach(function(listItem) {
      if(listItem.name ===  item ){
        return setSubCategories(listItem.subCategory_ids)
      }
   });
  };



  childs?.data?.forEach(function(item) {
    var name = item.name;
    categories?.unshift(name)
   
  });

  

  return (
    
           <div className='dropdown-content'>
    <ul>

                    {                    
                    categories.map((item)=>(
                      <li className="categories" onMouseEnter={() => handleMouseEnter(item)} onMouseLeave={handleMouseLeave} key={item._id}>{item}</li>
                    ))
                    }
    </ul>
             
                    {
                      subMenu && subCategories && categories &&
                      <div className="sub-categories">
                      {subCategories.map((item)=>(
                      <li className="categories">{item.name}</li>
                    ))}
                    </div>
                    }
                   
                    </div>
  
  )
}

export default Menu
