import logo from '../assets/img/logo.svg'
import seachIcon from '../assets/img/search.svg'
import Button from './Button'
import Menu from './Menu';
import { useEffect, useState } from "react"
function Navbar() {
  const categories = {
    Category_1: '#',
    Category_2: '#',
    Category_3: '#',
  }
  const blogs = {
    blog_1: '#',
    blog_2: '#',
    blog_3: '#',
  }
  const [isMenuOpen, setMenuOpen] = useState(false);
  
  const [isBlogMenuOpen, setBlogMenuOpen] = useState(false);

  const handleMouseEnter = (e) => {
    console.log(e.textContent)
    setMenuOpen(true);
    setBlogMenuOpen(true);
  };

  const handleMouseLeave = () => {
    setMenuOpen(false);
    setBlogMenuOpen(false);
  };

  return (
    <div>
        <nav>
        <div className="container">
            <div className="navbar">
            <div className="logo">
                <img src={logo} alt=""/>
            </div>
            <div className="menu">
                <ul className="hover-menu-container">

                    <li className='dropdown' onMouseEnter={handleMouseEnter} onMouseLeave={handleMouseLeave}><a href="">Categories</a>
                
                    {isMenuOpen && (
      <Menu childs={categories}/>)}
        </li>
                    <li className='dropdown'  onMouseEnter={handleMouseEnter} onMouseLeave={handleMouseLeave}><a href="">Blog</a>
                    {isMenuOpen && (
      <Menu childs={blogs}/>)}
                    </li>
                   
                </ul>
            </div>
            <div className="search">
                <form>
                    <input type="text" id="search" placeholder=""/>
                  </form>
                  <div className="seach-icon">
                    <img src={seachIcon} alt=""/>
                  </div>
            </div>
            <div >
            <div className="nav-buttons">
           <Button type ='btn-light'>Login</Button>
           <Button type ='btn-dark'>Register</Button>
           </div>
        </div>
        </div>
        </div>
    </nav>
    </div>
  )
}

export default Navbar
